_MovieExportID+1,	textMenuProc,	~0b10,	enabled,	"FPS",	{	/* array: 9 elements */		/* [1] */		"Best", noIcon, noKey, noMark, plain,		/* [2] */		"-", noIcon, noKey, noMark, plain,		/* [3] */		"8", noIcon, noKey, noMark, plain,		/* [4] */		"10", noIcon, noKey, noMark, plain,		/* [5] */		"12", noIcon, noKey, noMark, plain,		/* [6] */		"15", noIcon, noKey, noMark, plain,		/* [7] */		"24", noIcon, noKey, noMark, plain,		/* [8] */		"25", noIcon, noKey, noMark, plain,		/* [9] */		"30", noIcon, noKey, noMark, plain	}};resource 'MENU' (kEI_MovieExportID) {	kEI_MovieExportID,	textMenuProc,	~0b10,	enabled,	"Depth",	{	/* array: 7 elements */		/* [1] */		"Best Depth", noIcon, noKey, noMark, plain,		/* [2] */		"-", noIcon, noKey, noMark, plain,		/* [3] */		"Black and White", noIcon, noKey, noMark, plain,		/* [4] */		"256 Grays", noIcon, noKey, noMark, plain,		/* [5] */		"256 Colors", noIcon, noKey, noMark, plain,		/* [6] */		"Thousands of colors", noIcon, noKey, noMark, plain,		/* [7] */		"Millions of colors+", noIcon, noKey, noMark, plain	}};